c     x,              pres(ii),ht_ft(ii)
c     x,              t_prcn(ii),ob_t(ii),xiv_t(ii),ichk_t(ii)
c     x,              ob_q(ii),xiv_q(ii),ichk_q(ii)
c     x,              ob_dir(ii),xiv_d(ii),ichk_d(ii)
