            stuck = .true.
            k_stuck = 0
            stk_temp = -999 999
            iobfirst = -999 999
            ioblast = -999 999
            iifirst = -999 999
            iilast = -999 999
            ob_min =  999 999
            ob_max = -999 999
            ht_min_stuck =  999 999
            ht_max_stuck = -999 999
