c                iilast = ii
                if(ob_t(ii).lt.ob_min.and.
     $             ob_t(ii).ne.amiss) ob_min = ob_t(ii)
                if(ob_t(ii).gt.ob_max.and.
     $             ob_t(ii).ne.amiss) ob_max = ob_t(ii)
