              do iob=istart,iend
                ii = indx(iob)
                if(c_qc(ii)(6:6).ne.'B'.and.
     $             c_qc(ii)(6:6).ne.'b'.and.
     $             c_qc(ii)(6:6).ne.'E'.and.
     $             c_qc(ii)(6:6).ne.'I') c_qc(ii)(6:6) = 'K'
              enddo
cc
cc           Otherwise, if only a portion of the flight is stuck, set QC flags
