c           Mark as bad for other types
c           ----------------------------------------------------------------
            if(temp_min.gt.266.0.and.
     $         temp_max.lt.278.0.and.
     $         (.not.stuck).and.
     $         ht_max.gt.25000.) then
c
              do mm=1,kreg
                if(c_acftreg(iistart).eq.creg_reg(mm)) mreg = mm
              enddo
c
              do iob=istart,iend
                ii = indx(iob)
                if(itype(ii).eq.i_acars.or.
