                  elseif(itype(ii).eq.i_acars.or.
     $                   itype(ii).eq.i_acars_lvl.or.
     $                   itype(ii).eq.i_acars_asc.or.
     $                   itype(ii).eq.i_acars_des) then
                    nwhol_reg(mreg,2) = nwhol_reg(mreg,2) + 1
