                  elseif(itype(ii).eq.i_amdar.or. 
     $                   itype(ii).eq.i_amdar_lvl.or.
     $                   itype(ii).eq.i_amdar_asc.or.
     $                   itype(ii).eq.i_amdar_des) then
                    nwhol_reg(mreg,3) = nwhol_reg(mreg,3) + 1
