                if(iobfirst.eq.-999 999) then
                  iobfirst = iob-1
                  if(ob_dir(iim1).lt.ob_min.and.
     $               ob_dir(iim1).ne.amiss) ob_min = ob_dir(iim1)
                  if(ob_dir(iim1).gt.ob_max.and.
     $               ob_dir(iim1).ne.amiss) ob_max = ob_dir(iim1)
