c   
      subroutine ordchek_qc(numreps,max_reps,indx,csort,imiss,amiss
     $,          idt_near,idt_updn,htdif_same,c_acftreg,c_acftid,cidmiss
     $,          c_qc,alat,alon,pres,ht_ft,t_prcn,ob_t,ob_q,ob_dir
