      integer      nobs_flt(maxflt)    ! number of reports per flight
     $,            ntot_flt(maxflt)    ! total number of reports per flight
     $,            iobs_flt(maxflt)    ! index for first report in each flight
     $,            kflight             ! number of flights in dataset
