                  else
                    indx_save(ll) = iip1
                    knt_bad = knt_bad + 1
                  endif
c
                  c_qc(iip2)(1:1) = 'P'
                  c_qc(iip2)(3:4) = 'II'
                  ll = ll + 1
                  if(ll.gt.llim) then
                    write(io8,*) 'll limit exceeded--indx not saved!'
