                  else
                    indx_save(ll) = iip2
                    knt_bad = knt_bad + 1
                    last_bad = iip2
                    last_bad_m1 = iip1
c                    uwind_last = uwindp2
c                    vwind_last = vwindp2
                    wspd_last = ob_spd(iip2)
                    wdir_last = ob_dir(iip2)
                  endif
c
                  l_ii_pspd_ok = .false.
c
                  l_print = .true.
                  if(l_print) then
                    write(io8,*)
                    write(io8,*) 'Air speed--',airspdp1,
