                  else
                    pdirp2 = atan2(upspdp2,vpspdp2) / d2r + 180.0
                  endif
                  distp2 = distp2 / 1000.0
                else
                  udistp2 = amiss
                  vdistp2 = amiss
                  distp2  = amiss
                  upspdp2 = amiss
                  vpspdp2 = amiss
                  pspdp2  = amiss
                  pdirp2  = amiss
