                    vpspdp3 = vdistp3 / float(idt_difp3+60)
                    pspdp3  = distp3  / float(idt_difp3+60)
                  endif
                  if(upspdp3.eq.0.0.and.vpspdp3.eq.0.0) then
                    pdirp3 = 0.0
                  else
                    pdirp3 = atan2(upspdp3,vpspdp3) / d2r + 180.0
                  endif
                  distp3 = distp3 / 1000.0
                else
                  udistp3 = amiss
                  vdistp3 = amiss
                  distp3  = amiss
                  upspdp3 = amiss
                  vpspdp3 = amiss
                  pspdp3  = amiss
