                else
                  udist_wop1 = amiss
                  vdist_wop1 = amiss
                  dist_wop1  = amiss
                  upspd_wop1 = amiss
                  vpspd_wop1 = amiss
                  pspd_wop1  = amiss
                  pdir_wop1  = amiss
                endif
