                else
                  udist_wop2 = amiss
                  vdist_wop2 = amiss
                  dist_wop2  = amiss
                  upspd_wop2 = amiss
                  vpspd_wop2 = amiss
                  pspd_wop2  = amiss
                  pdir_wop2  = amiss
                endif
c
              else
                udist_wop2 = amiss
                vdist_wop2 = amiss
                dist_wop2  = amiss
                upspd_wop2 = amiss
                vpspd_wop2 = amiss
                pspd_wop2  = amiss
                pdir_wop2  = amiss
